import React from "react";


//I may need to link the search area with the API or data

const Search = (props) => {
  return(
  <div> 
  <label htmlFor="search"> Search by Name</label>
  <input type = "text" value = {props.search} onChange = {props.employeeFilterOnChange}></input>
  <datalist id="name">

    {props.users.map(user => (
            <option value={user} key={user} />
          ))}
  </datalist>
  <button type="submit" onClick = {props.handleFormSubmit} className= "btn btn-sucess" value= {props.search}></button>
  </div>
  )
};

export default Search;
