import React from "react";


//I may need to link the search area with the API or data

const Search = (props) => {
  return(
  <div> 
  <label htmlFor="search" placeholder= "Search by Name"> Search by Name</label>
  <input type = "text" value = {props.search} onChange = {props.employeeFilterOnChange}></input>

  <button type="submit" onClick = {props.handleFormSubmit} className= "btn btn-success" value= {props.search}>Search</button>
  </div>
  )
};

export default Search;
