       const usersMap = users.map((user,index) => {
           return (
                <tr key={index}>
                    <th scope="row">{index}</th>
                    <td>
                        <img src={user.picture.thumbnail}  /> 
                    </td>
                    <td>{`${user.name.first} ${user.name.last}`}</td>
                    <td>{user.gender}</td>
                    <td>{user.email}</td>
                </tr>
           )
       })

        return (

            <Table striped>
            <thead>
                <tr>
                    <th>Picture</th>
                    <th>Name</th>
                    <th>Gender</th>
                    <th>Email</th>
                </tr>
            </thead>
            <tbody>
                {usersMap}
            </tbody>
            </Table>

        );

    }
}
