import React from "react";
import Search from "./Search";
import API, { APIURL} from "../utils/API"; // using the API
import axios from "axios";
import { Table } from 'reactstrap';


const PersonList =(props)=> {

       const usersMap = props.users.map((user,index) => {
           return (
                <tr key={index}>
                    <th scope="row">{index+1}</th>
                    <td>
                        <img src={user.picture.thumbnail}  /> 
                    </td>
                    <td>{`${user.name.first} ${user.name.last}`}</td>
                    <td>{user.gender}</td>
                    <td>{user.email}</td>
                </tr>
           )
       })

        return (

            <Table striped>
            <thead>
                <tr>
                    <th>Picture</th>
                    <th>Name</th>
                    <th>Gender</th>
                    <th>Email</th>
                </tr>
            </thead>
            <tbody>
                {usersMap}
            </tbody>
            </Table>

        );

}

export default PersonList;
